import React, { useState } from 'react';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { withStyles } from '@material-ui/core/styles';
import { createEntry } from '../../../../services/EntryServices/entryService';

const styles = theme => ({
  paper: {
    maxWidth: 936,
    margin: 'auto',
    overflow: 'hidden',
  },
  enterBar: {
    borderBottom: '1px solid rgba(0, 0, 0, 0.12)',
    alignItems: 'center',
  },
  searchInput: {
    fontSize: theme.typography.fontSize,
  },
  block: {
    display: 'block',
  },
  textfield: {
    width: '95%',
  },
  contentWrapper: {
    margin: '40px 16px',
  },
  grid: {
    alignItems: 'center',
  }
});

const handleAddEntry = (text) => {
  var tempDate = new Date();
  var date = tempDate.getFullYear() + '-' + (tempDate.getMonth()+1) + '-' + tempDate.getDate() +' '+ tempDate.getHours()+':'+ tempDate.getMinutes()+':'+ tempDate.getSeconds();
  if(localStorage.getItem('uid')) {
    createEntry(localStorage.getItem('uid'), date, text);
    alert('Your entry was successfully recorded');
    window.location.reload(false);
  }
  else(alert('You are not registered to make a journal entry'));
};

function Entries(props) {
  const { classes } = props;

  const [text, setText] = useState("");

  return (
    <Paper className={classes.paper}>
      <Grid container>
        <Grid item xs alignItems="center">
          <TextField
            //className={classes.textfield}
            id="standard-multiline-static"
            margin="dense"
            label="New Entry"
            //multiline
            rows="10"
            onChange={(event)=> setText(event.target.value)}
          />
        </Grid>
      </ Grid>
      <AppBar className={classes.enterBar} position="static" color="default" elevation={0}>
        <Toolbar>
          <Grid container spacing={2} alignItems="center">
            <Grid item>
              <Button
                variant="contained" 
                color="primary" 
                onClick={() => {handleAddEntry(text)}}>
                  Add entry
              </Button>
            </Grid>
          </Grid>
        </Toolbar>
      </AppBar>
    </Paper>
  );
}

Entries.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Entries);
