import React, { Fragment, useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import { Typography } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import { getUser } from '../../../../services/UserServices/userServices';

const styles = theme => ({
    paper: {
      maxWidth: 936,
      margin: 'auto',
      overflow: 'hidden',
      marginBottom: '10px',
    },
      root: {
        margin: theme.spacing(1),
        marginTop: '12px',
        width: 750,
        marginLeft: '20px',
    },
    enterBar: {
        borderBottom: '1px solid rgba(0, 0, 0, 0.12)',
        alignItems: 'center',
      },
  });

function Profile(props){
    const {classes} =  props;

    const [ profileState, setProfileState ] = useState({
        firstname: '',
        lastname: '',
        email: '',
        outreach: false
    });

    useEffect(() => {
        if(localStorage.getItem('uid')){
          getUser(localStorage.getItem('uid')).then(res => {
            const user=res.data[0];
            if(res) setProfileState({firstname: user.firstname, lastname: user.lastname, email: user.email, outreach: user.outreach})
          })
        }
      }, []);

    const handleLogout = () => {
        localStorage.clear();
        window.location.reload(false);
    }

    return (
        <Paper className={classes.paper}>
            <div className= {classes.root}>
                <Typography >
                    <div>
                        {"First Name: " + profileState.firstname}
                    </div>
                    <div>
                        {"Last Name: " + profileState.lastname}
                    </div>
                    <div>
                        {"Primary Email Address: " + profileState.email}
                    </div>
                    <div>
                        {"Email preferences: " + profileState.outreach? "Subscribed to notifications and reminders": "Not subscribed to notifications or reminders"}
                    </div>
                </ Typography>
            </div>
            <AppBar className={classes.enterBar} position="static" color="default" elevation={0}>
                <Toolbar>
                    <Grid container spacing={2} alignItems="center">
                        <Grid item>
                            <Button
                                variant="contained" 
                                onClick={handleLogout}>
                                Logout
                            </Button>
                        </Grid>
                    </Grid>
                </Toolbar>
            </AppBar>
        </Paper>
    )
}

export default withStyles(styles)(Profile);
