import React, { useState, useEffect, Fragment } from 'react';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import { Typography, TextField } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import { getUser } from '../../../../../services/UserServices/userServices';

const styles = theme => ({
    paper: {
      maxWidth: 936,
      margin: 'auto',
      overflow: 'hidden',
      marginBottom: '10px',
    },
      root: {
        margin: theme.spacing(1),
        marginTop: '12px',
        marginLeft: '20px',
    },
    enterBar: {
        borderBottom: '1px solid rgba(0, 0, 0, 0.12)',
        alignItems: 'flex-end',
      },
  });

function EditBox(props){
    const {classes} =  props;

    const [ editBoxState, setBoxState ] = useState({
        firstname: '',
        lastname: '',
        email: '',
        outreach: false
    });

    useEffect(() => {
        if(localStorage.getItem('uid')){
          getUser(localStorage.getItem('uid')).then(res => {
            const user=res.data[0];
            if(res) setBoxState({firstname: user.firstname, lastname: user.lastname, email: user.email, outreach: user.outreach})
          })
        }
    }, []);

    return (
        <Paper className={classes.paper}>
            <div className= {classes.root}>
                <TextField value={editBoxState.firstname} 
                    fullWidth 
                    onChange={(event)=>setBoxState({firstname: event.target.value})}
                    label="First Name"/> 
                <TextField value={editBoxState.lastname} 
                    fullWidth 
                    onChange={(event)=>setBoxState({firstname: event.target.value})}
                    label="Last Name"/>
                <TextField value={editBoxState.email} fullWidth onChange={(event)=>setBoxState({firstname: event.target.value})}/>  
                <TextField value={editBoxState.outreach} fullWidth onChange={(event)=>setBoxState({firstname: event.target.value})}/> 
            </div>
            <AppBar className={classes.enterBar} position="static" color="default" elevation={0}>
                <Toolbar>
                    <Grid container spacing={2}>
                        <Grid item>
                            <Button
                                variant="contained"
                                color="primary">
                                Done
                            </Button>
                            <Button
                                variant="contained"
                                onClick={props.toggle}>
                                Cancel
                            </Button>
                        </Grid>
                    </Grid>
                </Toolbar>
            </AppBar>
        </Paper>
    )
}

export default withStyles(styles)(EditBox);
