import React, { useState } from 'react';
import EditBox from './Boxes/EditBox';
import SettingsBox from './Boxes/SettingsBox';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    paper: {
      maxWidth: 936,
      margin: 'auto',
      overflow: 'hidden',
      marginBottom: '10px',
    },
      root: {
        margin: theme.spacing(1),
        marginTop: '12px',
        marginLeft: '20px',
    },
    enterBar: {
        borderBottom: '1px solid rgba(0, 0, 0, 0.12)',
        alignItems: 'flex-end',
      },
  });

function Settings(){
    // const {classes} =  props;

    const [ settingState, setSettingState ] = useState({
        showEditBox: false
    });

    const handleEdit = () => {
        const toggle = !settingState.showEditBox;
        setSettingState({showEditBox: toggle});
    }

    const box = settingState.showEditBox ? <EditBox toggle={handleEdit}/> : <SettingsBox toggle={handleEdit}/>
    return (
        <div>
            <Typography variant="h6" alignItems="center"> Account Information</Typography>
            {box}
        </div>
    )
}

export default withStyles(styles)(Settings);
