import axios from 'axios'

const getUser = async (id) => {
    return await axios({
        method: 'get',
        url: 'http://localhost:3001/api/users/getUser/'.concat(id), //change this url later
    })
    .then(response => {
        //console.log('[response data]: '.concat(response.data[0].firstname));
        return response.data[0].firstname;
    })
    .catch(error => console.log(error));
}

const createUser = (firstname, lastname, email, pass, outreach) => {
    axios({
        method: 'post',
        url: 'http://localhost:3001/api/users/addUser',
        data: {
            "firstname": firstname,
            "lastname": lastname,
            "email": email,
            "pass": pass,
            "outreach": outreach
        }
    }).catch(error => console.log(error));
}

const updateUser = (id, firstname, lastname, email, pass, outreach) => {
    axios({
        method: 'put',
        url: 'http://localhost:3001/api/users/updateUser',
        data: {
            "id": id,
            "firstname": firstname,
            "lastname": lastname,
            "email": email,
            "pass": pass,
            "outreach": outreach
        }
    }).catch(error => console.log(error));
}

export {getUser, createUser, updateUser}
