import React, { useState, useEffect } from 'react';
import Paper from '@material-ui/core/Paper';
import { Typography } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import { getUser } from '../../../../services/UserServices/userServices';

const styles = theme => ({
    paper: {
      maxWidth: 936,
      margin: 'auto',
      overflow: 'hidden',
      marginBottom: '10px',
    },
      root: {
        margin: theme.spacing(1),
        marginTop: '12px',
        width: 750,
        marginLeft: '20px',
    },
  });

function Profile(props){
    const {classes} =  props;

    const [ profileState, setProfileState ] = useState({
        firstname: '',
        lastname: '',
        email: '',
        outreach: false
    });

    useEffect(() => {
        if(localStorage.getItem('uid')){
          getUser(localStorage.getItem('uid')).then(res => {
            const user=res.data[0];
            if(res) setProfileState({firstname: user.firstname, lastname: user.lastname, email: user.email, outreach: user.outreach})
          })
        }
      }, []);

    // var user = null;
    // if(localStorage.getItem('uid')) getUser(localStorage.getItem('uid')).then(res => {
    //     console.log(res)
    //     if(res) user = res.data[0];
    // });
    return (
        <Paper className={classes.paper}>
        <div className= {classes.root}>
            <Typography >
                {profileState.firstname}
            </ Typography>
          </div>
        </ Paper>
    )
}

export default withStyles(styles)(Profile);
